import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import eventList from './data/list.json';

const Home = () => {
  const navigate = useNavigate();
  const [events, setEvents] = useState([]);
  const [selectedYear, setSelectedYear] = useState(null);

  // ‚úÖ Naƒçti seznam event≈Ø z list.json
  useEffect(() => {
    const data = eventList;
    setEvents(data);
    // Vyber nejnovƒõj≈°√≠ rok automaticky
    const years = [...new Set(data.map((event) => event.year))].sort((a, b) => b - a);
    setSelectedYear(years[0]);
  }, []); // KONEC useEffect, ≈æ√°dn√Ω catch!

  const uniqueYears = [...new Set(events.map((event) => event.year))].sort((a, b) => b - a);

  return (
    <div className="home-container">
      {/* üîù Horn√≠ li≈°ta */}
      <div className="header">Hello, Cycling World! üö¥</div>

      {/* üìÖ Li≈°ta s roky */}
      <div className="year-buttons">
        {uniqueYears.map((year) => (
          <button
            key={year}
            onClick={() => setSelectedYear(year)}
            className={`button ${year === selectedYear ? "active" : ""}`}
          >
            {year}
          </button>
        ))}
      </div>

      {/* üìÖ Seznam event≈Ø */}
      <div className="events-container">
        {events
          .filter((event) => event.year === selectedYear)
          .map((event, index) => (
            <div
              key={index}
              className="event-card"
              onClick={() => navigate(`/event/${event.file.replace('.json', '')}`)}
            >
              <span className="event-name">{event.name}</span> - {event.date}
            </div>
          ))}
      </div>

      {/* üîª Spodn√≠ li≈°ta */}
      <div className="footer">‚ù§Ô∏è Enjoy your ride! ‚ù§Ô∏è </div>
    </div>
  );
};

export default Home;
