import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

const API_KEY = "AIzaSyAth7L9k8Xmpl9e5GSfeW68N1ThaSp0WAs"; // ✅ Nahraď svým API klíčem

const eventFiles = require.context("./events", false, /\.json$/);
const eventsData = eventFiles.keys().map((key) => eventFiles(key));

const DisciplineLive = () => {
  const { eventName, disciplineName } = useParams();
  const navigate = useNavigate();

  const [eventDisplayName, setEventDisplayName] = useState(""); // ✅ Správný název eventu
  const [displayName, setDisplayName] = useState(""); // ✅ Správný název disciplíny
  const [data, setData] = useState([]);
  const [headers, setHeaders] = useState([]);
  const [googleSheetsId, setGoogleSheetsId] = useState("");
  const [sheetName, setSheetName] = useState("");
  const [rowRange, setRowRange] = useState([0, 0]);
  const [selectedColumns, setSelectedColumns] = useState([]);
  const [relatedEvents, setRelatedEvents] = useState([]);
  const [boldRows, setBoldRows] = useState([]); // ✅ Nový state pro tučné řádky


  // 1️⃣ **Načtení eventu a disciplíny z JSON souboru**
  useEffect(() => {
    const fetchEventData = async () => {
      try {
        const event = eventsData.find(
          (e) => e.name.replace(/\s+/g, "-").toLowerCase() === eventName
        );

        if (event) {
          setEventDisplayName(event.name); // ✅ Ukládáme správný název eventu
          setGoogleSheetsId(event.googleSheetsId);

          const discipline = event.disciplines
            .flatMap((day) => day.events)
            .find((d) => {
              const formattedName = d.name.replace(/\s+/g, "-").replace(/\//g, "-").toLowerCase();
              return formattedName === disciplineName;
            });

          if (discipline) {
            setSheetName(discipline.sheetName);
            setRowRange(discipline.rowRange);
            setSelectedColumns(discipline.columns);
            setRelatedEvents(discipline.relatedEvents || []);
            setDisplayName(discipline.displayName || discipline.name);
            setBoldRows(discipline.boldRows || []); // ✅ Uložíme pole tučných řádků



          console.log("📌 Načtené boldRows:", discipline.boldRows); 
          } else {
            console.error("🚨 Disciplína nenalezena:", disciplineName);
          }
        } else {
          console.error("🚨 Event nenalezen:", eventName);
        }
      } catch (error) {
        console.error("❌ Chyba při načítání JSON:", error);
      }
    };

    fetchEventData();
  }, [eventName, disciplineName]);

  // 2️⃣ **Načítání dat z Google Sheets**
  useEffect(() => {
    if (!googleSheetsId || !sheetName || rowRange[0] === 0 || selectedColumns.length === 0) {
      console.warn("⚠️ Chybí klíčové údaje pro načtení dat.");
      return;
    }

    const fetchDataFromSheets = async () => {
      const range = `${sheetName}!A${rowRange[0]}:Z${rowRange[1]}`;
      const url = `https://sheets.googleapis.com/v4/spreadsheets/${googleSheetsId}/values/${range}?key=${API_KEY}`;

      try {
        const response = await fetch(url);
        const data = await response.json();

        if (!data.values) {
          console.error("❌ Žádná data nebyla nalezena pro rozsah:", range);
          return;
        }

        const columnIndexes = selectedColumns.map((col) => {
          const colIndex = col.charCodeAt(0) - 65;
          return colIndex >= 0 ? colIndex : -1;
        });

        const allHeaders = data.values[0];
        const selectedHeaders = columnIndexes.map((index) => allHeaders[index]);
        setHeaders(selectedHeaders);

        const formattedData = data.values.slice(1).map((row) =>
          columnIndexes.map((index) => (index !== -1 ? row[index] : ""))
        );
        setData(formattedData);

      } catch (error) {
        console.error("❌ Chyba při načítání dat z Google Sheets:", error);
      }
    };

    fetchDataFromSheets();
    const interval = setInterval(fetchDataFromSheets, 30000);
    return () => clearInterval(interval);
  }, [googleSheetsId, sheetName, rowRange, selectedColumns]);

  return (
    <div className="p-4">
      {/* 🔙 Tlačítko zpět - OPRAVENÉ */}
      <button onClick={() => navigate(`/event/${eventName}`)}>
        ⬅️ Back to {eventDisplayName || eventName.replace(/-/g, " ")}
      </button>

      {/* 🏆 Název disciplíny */}
      <h1 className="text-3xl font-bold mt-4">{displayName}</h1>
      <h3 className="text-gray-600">Results</h3>

      {/* 🔀 Přepínače mezi jednotlivými fázemi disciplíny */}
      {relatedEvents.length > 0 && (
        <div className="mt-4">
          <div className="flex gap-2 mt-2">
            {relatedEvents.map((relatedEvent, index) => {
              const formattedRelatedEvent = relatedEvent.replace(/\s+/g, "-").replace(/\//g, "-").toLowerCase();
              return (
                <button
                  key={index}
                  className="px-3 py-1 bg-gray-300 rounded"
                  onClick={() => navigate(`/event/${eventName}/discipline/${formattedRelatedEvent}`)}
                >
                  {relatedEvent}
                </button>
              );
            })}
          </div>
        </div>
      )}

      {/* 📊 Tabulka výsledků */}
      {data.length > 0 ? (
        <table className="w-full border-collapse border border-gray-300 mt-4">
          <thead>
            <tr className="bg-gray-200">
              {headers.map((header, index) => (
                <th key={index} className="border border-gray-300 p-2">
                  {header}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
  {data.map((row, i) => {
    const rowNumber = rowRange[0] + i; // Skutečné číslo řádku v tabulce
    const isBold = boldRows.includes(rowNumber); // ✅ Kontrola, zda je řádek mezi tučnými
    console.log(`📝 Řádek ${rowNumber}: ${isBold ? "TUČNÝ" : "normální"}`); // 🔍 Debug výstup

    return (
      <tr key={i} className={`text-center ${boldRows.includes(rowNumber) ? "font-bold" : ""}`}>
        {row.map((cell, j) => (
          <td key={j} className="border border-gray-300 p-2">
            {cell}
          </td>
        ))}
      </tr>
    );
  })}
</tbody>

        </table>
      ) : (
        <p className="text-red-500 mt-4">⚠️ Žádná data k zobrazení.</p>
      )}
    </div>
  );
};

export default DisciplineLive;
