import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

const API_KEY = "TVUJ_GOOGLE_API_KLIC"; // ‚úÖ Nahraƒè sv√Ωm API kl√≠ƒçem

const eventFiles = require.context("./events", false, /\.json$/);
const eventsData = eventFiles.keys().map((key) => eventFiles(key));

const DisciplineLive = () => {
  const { eventName, disciplineName } = useParams();
  const navigate = useNavigate();

  const [displayName, setDisplayName] = useState(""); // ‚úÖ Spr√°vnƒõ inicializovan√© useState
  const [data, setData] = useState([]);
  const [headers, setHeaders] = useState([]);
  const [googleSheetsId, setGoogleSheetsId] = useState("");
  const [sheetName, setSheetName] = useState("");
  const [rowRange, setRowRange] = useState([0, 0]);
  const [selectedColumns, setSelectedColumns] = useState([]);
  const [relatedEvents, setRelatedEvents] = useState([]); // üÜï P≈ôid√°no pro propojen√© f√°ze

  // 1Ô∏è‚É£ **Naƒçten√≠ eventu a discipl√≠ny z JSON souboru**
  useEffect(() => {
    const fetchEventData = async () => {
      try {
        const event = eventsData.find(
          (e) => e.name.replace(/\s+/g, "-").toLowerCase() === eventName
        );

        if (event) {
          setGoogleSheetsId(event.googleSheetsId);

          const discipline = event.disciplines
            .flatMap((day) => day.events)
            .find((d) => {
              const formattedName = d.name.replace(/\s+/g, "-").replace(/\//g, "-").toLowerCase();
              return formattedName === disciplineName;
            });

          if (discipline) {
            setSheetName(discipline.sheetName);
            setRowRange(discipline.rowRange);
            setSelectedColumns(discipline.columns);
            setRelatedEvents(discipline.relatedEvents || []);
            setDisplayName(discipline.displayName || discipline.name); // ‚úÖ Opraveno ‚Äì naƒç√≠t√°me spr√°vnƒõ
          } else {
            console.error("üö® Discipl√≠na nenalezena:", disciplineName);
          }
        } else {
          console.error("üö® Event nenalezen:", eventName);
        }
      } catch (error) {
        console.error("‚ùå Chyba p≈ôi naƒç√≠t√°n√≠ JSON:", error);
      }
    };

    fetchEventData();
  }, [eventName, disciplineName]);

  // 2Ô∏è‚É£ **Naƒç√≠t√°n√≠ dat z Google Sheets**
  useEffect(() => {
    if (!googleSheetsId || !sheetName || rowRange[0] === 0 || selectedColumns.length === 0) {
      console.warn("‚ö†Ô∏è Chyb√≠ kl√≠ƒçov√© √∫daje pro naƒçten√≠ dat.");
      return;
    }

    const fetchDataFromSheets = async () => {
      const range = `${sheetName}!A${rowRange[0]}:Z${rowRange[1]}`;
      const url = `https://sheets.googleapis.com/v4/spreadsheets/${googleSheetsId}/values/${range}?key=${API_KEY}`;

      try {
        const response = await fetch(url);
        const data = await response.json();

        if (!data.values) {
          console.error("‚ùå ≈Ω√°dn√° data nebyla nalezena pro rozsah:", range);
          return;
        }

        // ‚úÖ P≈ôevod sloupc≈Ø z p√≠smen (A, B, C...) na indexy
        const columnIndexes = selectedColumns.map((col) => {
          const colIndex = col.charCodeAt(0) - 65;
          return colIndex >= 0 ? colIndex : -1;
        });

        // ‚úÖ Nastav√≠me z√°hlav√≠ tabulky spr√°vnƒõ podle prvn√≠ho ≈ô√°dku
        const allHeaders = data.values[0];
        const selectedHeaders = columnIndexes.map((index) => allHeaders[index]);
        setHeaders(selectedHeaders);

        // ‚úÖ Naƒçteme data od druh√©ho ≈ô√°dku (abychom vynechali z√°hlav√≠)
        const formattedData = data.values.slice(1).map((row) =>
          columnIndexes.map((index) => (index !== -1 ? row[index] : ""))
        );
        setData(formattedData);

      } catch (error) {
        console.error("‚ùå Chyba p≈ôi naƒç√≠t√°n√≠ dat z Google Sheets:", error);
      }
    };

    fetchDataFromSheets();
    const interval = setInterval(fetchDataFromSheets, 30000);
    return () => clearInterval(interval);
  }, [googleSheetsId, sheetName, rowRange, selectedColumns]);

  return (
    <div className="p-4">
      {/* üîô Tlaƒç√≠tko zpƒõt */}
      <button onClick={() => navigate(`/event/${eventName}`)}>
        ‚¨ÖÔ∏è Back to {eventName.replace(/-/g, " ")}
      </button>

      {/* üèÜ N√°zev discipl√≠ny */}
      <h1 className="text-3xl font-bold mt-4">{displayName}</h1>
      <p className="text-gray-600">Results</p>

      {/* üîÄ P≈ôep√≠naƒçe mezi jednotliv√Ωmi f√°zemi discipl√≠ny */}
      {relatedEvents.length > 0 && (
        <div className="mt-4">
          <h3 className="text-lg font-semibold">Next parts of the race:</h3>
          <div className="flex gap-2 mt-2">
            {relatedEvents.map((relatedEvent, index) => {
              const formattedRelatedEvent = relatedEvent.replace(/\s+/g, "-").replace(/\//g, "-").toLowerCase();
              return (
                <button
                  key={index}
                  className="px-3 py-1 bg-gray-300 rounded"
                  onClick={() => navigate(`/event/${eventName}/discipline/${formattedRelatedEvent}`)}
                >
                  {relatedEvent}
                </button>
              );
            })}
          </div>
        </div>
      )}

      {/* üìä Tabulka v√Ωsledk≈Ø */}
      {data.length > 0 ? (
        <table className="w-full border-collapse border border-gray-300 mt-4">
          <thead>
            <tr className="bg-gray-200">
              {headers.map((header, index) => (
                <th key={index} className="border border-gray-300 p-2">
                  {header}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {data.map((row, index) => (
              <tr key={index} className="text-center">
                {row.map((cell, cellIndex) => (
                  <td key={cellIndex} className="border border-gray-300 p-2">
                    {cell}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="text-red-500 mt-4">‚ö†Ô∏è ≈Ω√°dn√° data k zobrazen√≠.</p>
      )}
    </div>
  );
};

export default DisciplineLive;
