import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

const API_KEY = "AIzaSyAth7L9k8Xmpl9e5GSfeW68N1ThaSp0WAs";

const eventFiles = require.context("./events", false, /\.json$/);
const eventsData = eventFiles.keys().map((key) => eventFiles(key));

const DisciplineLive = () => {
  const { eventName, disciplineName } = useParams();
  const navigate = useNavigate();

  const [eventDisplayName, setEventDisplayName] = useState("");
  const [displayName, setDisplayName] = useState("");
  const [data, setData] = useState([]);
  const [headers, setHeaders] = useState([]);
  const [googleSheetsId, setGoogleSheetsId] = useState("");
  const [sheetName, setSheetName] = useState("");
  const [rowRange, setRowRange] = useState([0, 0]);
  const [selectedColumns, setSelectedColumns] = useState([]);
  const [relatedEvents, setRelatedEvents] = useState([]);
  const [boldRows, setBoldRows] = useState([]);

  useEffect(() => {
    const fetchEventData = async () => {
      try {
        const event = eventsData.find(
          (e) => e.name.replace(/\s+/g, "-").toLowerCase() === eventName
        );

        if (event) {
          setEventDisplayName(event.name);
          setGoogleSheetsId(event.googleSheetsId);

          const discipline = event.disciplines
            .flatMap((day) => day.events)
            .find((d) => {
              const formattedName = d.name.replace(/\s+/g, "-").replace(/\//g, "-").toLowerCase();
              return formattedName === disciplineName;
            });

          if (discipline) {
            setSheetName(discipline.sheetName);
            setRowRange(discipline.rowRange);
            setSelectedColumns(discipline.columns);
            setRelatedEvents(discipline.relatedEvents || []);
            setDisplayName(discipline.displayName || discipline.name);
            setBoldRows(discipline.boldRows || []);
          }
        }
      } catch (error) {
        console.error("‚ùå Chyba p≈ôi naƒç√≠t√°n√≠ JSON:", error);
      }
    };

    fetchEventData();
  }, [eventName, disciplineName]);

  useEffect(() => {
    if (!googleSheetsId || !sheetName || rowRange[0] === 0 || selectedColumns.length === 0) {
      return;
    }

    const fetchDataFromSheets = async () => {
      const range = `${sheetName}!A${rowRange[0]}:Z${rowRange[1]}`;
      const url = `https://sheets.googleapis.com/v4/spreadsheets/${googleSheetsId}/values/${range}?key=${API_KEY}`;

      try {
        const response = await fetch(url);
        const data = await response.json();

        if (!data.values) {
          return;
        }

        const columnIndexes = selectedColumns.map((col) => {
          const colIndex = col.charCodeAt(0) - 65;
          return colIndex >= 0 ? colIndex : -1;
        });

        const allHeaders = data.values[0];
        const selectedHeaders = columnIndexes.map((index) => allHeaders[index]);
        setHeaders(selectedHeaders);

        const formattedData = data.values.slice(1).map((row) =>
          columnIndexes.map((index) => (index !== -1 ? row[index] : ""))
        );
        setData(formattedData);
      } catch (error) {
        console.error("‚ùå Chyba p≈ôi naƒç√≠t√°n√≠ dat z Google Sheets:", error);
      }
    };

    fetchDataFromSheets();
    const interval = setInterval(fetchDataFromSheets, 30000);
    return () => clearInterval(interval);
  }, [googleSheetsId, sheetName, rowRange, selectedColumns]);

  return (
    <div className="p-4 discipline-container">
      <button onClick={() => navigate(`/event/${eventName}`)} className="back-button">
        ‚¨ÖÔ∏è Back to {eventDisplayName || eventName.replace(/-/g, " ")}
      </button>

      <h1 className="discipline-title">{displayName}</h1>
      <h3 className="discipline-subtitle">Results</h3>

      {relatedEvents.length > 0 && (
        <div className="related-events">
          {relatedEvents.map((relatedEvent, index) => {
            const formattedRelatedEvent = relatedEvent.replace(/\s+/g, "-").replace(/\//g, "-").toLowerCase();
            return (
              <button key={index} className="event-switch-button" onClick={() => navigate(`/event/${eventName}/discipline/${formattedRelatedEvent}`)}>
                {relatedEvent}
              </button>
            );
          })}
        </div>
      )}

      {/* üìä Tabulka v√Ωsledk≈Ø */}
      {data.length > 0 ? (
       <table className="discipline-table">
       <thead>
         <tr className="header-row">
           {headers.map((header, index) => (
             <th key={index}>{header}</th>
           ))}
         </tr>
       </thead>
       <tbody>
         {data.map((row, i) => {
           const rowNumber = rowRange[0] + i + 1;
           const isBold = boldRows.includes(rowNumber);

           // ‚úÖ Pr√°zdn√Ω ≈ô√°dek (mezera)
           const isSpacerRow = row.every(cell => (cell || "").trim() === "");
           if (isSpacerRow) {
             return (
               <tr key={`spacer-${i}`} className="spacer-row">
                 <td colSpan={headers.length}></td>
               </tr>
             );
           }

           // ‚úÖ Li≈°ta "Final for Bronze/Gold"
           const isFinalHeader = row.some(cell =>
             typeof cell === "string" && cell.trim().toLowerCase().startsWith("final for")
           );
           if (isFinalHeader) {
             return (
               <tr key={`final-${i}`} className="final-header-row">
                 <td colSpan={headers.length} className="final-header-cell">
                   {row.find(cell => cell).toUpperCase()}
                 </td>
               </tr>
             );
           }

           // ‚úÖ Norm√°ln√≠ data
           return (
             <tr key={i} className={isBold ? "bold-row" : ""}>
               {row.map((cell, j) => {
                 const isWinner = typeof cell === "string" && cell.trim().toUpperCase() === "WINNER";
                 return (
                   <td key={j} className={isWinner ? "winner-cell" : ""}>
                     {cell}
                   </td>
                 );
               })}
             </tr>
           );
         })}
       </tbody>
     </table>

      ) : (
        <p className="no-data">‚ö†Ô∏è ≈Ω√°dn√° data k zobrazen√≠.</p>
      )}
    </div>
  );
};

export default DisciplineLive;
