import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const EventDetail = () => {
  const { eventName } = useParams();
  const [eventData, setEventData] = useState(null);

  useEffect(() => {
    import(`./events/${eventName}.json`)
      .then((data) => setEventData(data.default))
      .catch((error) => console.error("Chyba při načítání dat:", error));
  }, [eventName]);

  if (!eventData) return <div>Loading...</div>;

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold">{eventData.name}</h1>
      <p className="text-lg text-gray-600">{eventData.date}</p>

      {/* Tlačítko pro PDF program */}
      <div className="my-4">
        <a href={eventData.schedule} target="_blank" rel="noopener noreferrer" className="bg-blue-500 text-white px-4 py-2 rounded">
          Event Schedule (PDF)
        </a>
      </div>

      {/* Seznam PDF souborů */}
      <div className="my-4">
        {eventData.pdfs.map((pdf, index) => (
          <a key={index} href={pdf.url} target="_blank" rel="noopener noreferrer" className="block text-blue-600">
            {pdf.name}
          </a>
        ))}
      </div>

      {/* Disciplíny */}
      {eventData.disciplines.map((day, index) => (
        <div key={index} className="my-4">
          <h2 className="text-2xl font-semibold cursor-pointer">{`Day ${day.day} - ${day.date}`}</h2>
          <div className="ml-4">
            {day.events.map((event, i) => (
              <div key={i} className="flex justify-between bg-gray-100 p-2 my-2 rounded">
                <span>{event.name}</span>
                <div>
                  {event.startList && (
                    <a href={event.startList} target="_blank" rel="noopener noreferrer" className="text-blue-500 mx-2">
                      Start List
                    </a>
                  )}
                  {event.results && (
                    <a href={event.results} target="_blank" rel="noopener noreferrer" className="text-green-500">
                      Results
                    </a>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default EventDetail;
