import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import eventData from "./events/gp-brno-sprint.json"; // Sem se bude naƒç√≠tat JSON pro ka≈æd√Ω event

const EventDetail = () => {
  const { eventName } = useParams();
  const navigate = useNavigate();

  return (
    <div className="p-4">
      {/* Tlaƒç√≠tko zpƒõt */}
      <button
        onClick={() => navigate(-1)}
        className="mb-4 p-2 bg-gray-300 rounded"
      >
        ‚Üê Back to Events
      </button>

      {/* N√°zev eventu */}
      <h1 className="text-3xl font-bold">{eventData.name}</h1>
      <p className="text-gray-600">{eventData.date}</p>

      {/* Odkaz na program */}
      <a href={eventData.schedule} target="_blank" rel="noopener noreferrer" className="text-blue-500 underline">
        üìÑ Event Schedule
      </a>

      {/* Seznam dn≈Ø */}
      {eventData.disciplines.map((day, dayIndex) => (
        <div key={dayIndex} className="mt-4">
          {/* Klikac√≠ nadpis dne */}
          <h2
            className="text-xl font-semibold cursor-pointer bg-gray-200 p-2"
            onClick={() => {
              document.getElementById(`day-${dayIndex}`).classList.toggle("hidden");
            }}
          >
            Day {day.day} - {day.date} ‚¨á
          </h2>

          {/* Seznam discipl√≠n */}
          <div id={`day-${dayIndex}`} className="hidden p-2">
            {day.events.map((event, eventIndex) => (
              <div key={eventIndex} className="flex justify-between bg-white p-2 my-1 shadow">
                {/* üî• Klikac√≠ n√°zev discipl√≠ny ‚Üí vede na Live v√Ωsledky */}
                <span
                  className="text-blue-500 cursor-pointer underline"
                  onClick={() => navigate(`/event/${eventName}/discipline/${event.name.replace(/\s+/g, "-").toLowerCase()}`)}
                >
                  {event.name}
                </span>

                {/* PDF tlaƒç√≠tka pro StartList & Results */}
                <div>
                  {event.startList && (
                    <a href={event.startList} target="_blank" rel="noopener noreferrer" className="ml-2 bg-gray-300 p-1 rounded">
                      üìÑ StartList
                    </a>
                  )}
                  {event.results && (
                    <a href={event.results} target="_blank" rel="noopener noreferrer" className="ml-2 bg-gray-300 p-1 rounded">
                      üèÜ Results
                    </a>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default EventDetail;
