import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

const eventData = {
  "gp-brno-sprint": {
    name: "GP Brno Sprint",
    date: "06/2024",
    eventSchedule: "https://drive.google.com/example.pdf",
    days: [
      {
        day: "Day 1",
        date: "06/06/2024",
        disciplines: [
          { name: "Sprint ME Qualification", live: true, startList: "https://drive.google.com/startlist.pdf", results: "https://drive.google.com/results.pdf" },
          { name: "Sprint ME 1/16-final", live: true, startList: "", results: "" },
          { name: "Keirin ME Heats", live: true, startList: "", results: "" }
        ]
      },
      {
        day: "Day 2",
        date: "06/07/2024",
        disciplines: [
          { name: "Sprint ME Finals", live: true, startList: "", results: "" },
          { name: "Keirin ME Finals", live: true, startList: "", results: "" }
        ]
      }
    ]
  }
};

const EventDetail = () => {
  const { eventId } = useParams();
  const navigate = useNavigate();
  const event = eventData[eventId];
  const [expandedDays, setExpandedDays] = useState({});

  if (!event) {
    return <div className="p-4 text-center text-red-500">Event not found</div>;
  }

  const toggleDay = (index) => {
    setExpandedDays((prev) => ({ ...prev, [index]: !prev[index] }));
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      {/* Back Button */}
      <button onClick={() => navigate(-1)} className="mb-4 text-blue-500 underline">
        ← Back to Events
      </button>

      {/* Event Title */}
      <h1 className="text-3xl font-bold">{event.name}</h1>
      <p className="text-lg text-gray-600">{event.date}</p>

      {/* Event Schedule PDF */}
      <a
        href={event.eventSchedule}
        target="_blank"
        rel="noopener noreferrer"
        className="block mt-2 p-2 bg-blue-500 text-white text-center rounded"
      >
        📄 Event Schedule (PDF)
      </a>

      {/* Days & Disciplines */}
      {event.days.map((day, index) => (
        <div key={index} className="mt-4">
          <button
            onClick={() => toggleDay(index)}
            className="w-full text-left bg-gray-300 p-2 font-semibold"
          >
            {day.day} - {day.date} {expandedDays[index] ? "▲" : "▼"}
          </button>
          {expandedDays[index] && (
            <ul className="bg-white p-4 shadow-md">
              {day.disciplines.map((discipline, dIndex) => (
                <li key={dIndex} className="p-2 border-b">
                  <span className="font-bold">{discipline.name}</span>
                  {discipline.live && (
                    <button className="ml-4 text-green-600 underline">Live</button>
                  )}
                  <span className="ml-4">
                    {discipline.startList && (
                      <a
                        href={discipline.startList}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-blue-500 underline"
                      >
                        StartList
                      </a>
                    )}
                    {discipline.results && (
                      <a
                        href={discipline.results}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="ml-2 text-blue-500 underline"
                      >
                        Results
                      </a>
                    )}
                  </span>
                </li>
              ))}
            </ul>
          )}
        </div>
      ))}
    </div>
  );
};

export default EventDetail;
