import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const eventsByYear = {
  2025: [
    { name: "Mistrovstv√≠ Evropy", date: "08/2025", hidden: false },
    { name: "Mistrovstv√≠ ƒåR", date: "06/2025", hidden: false },
    { name: "GP Framar Praha", date: "05/2025", hidden: false }
  ],
  2024: [
    { name: "GP Brno Sprint", date: "06/2024", hidden: false },
  ],
};

const Home = () => {
  const navigate = useNavigate();
  const [selectedYear, setSelectedYear] = useState(
    Math.max(...Object.keys(eventsByYear).map(Number))
  );

  return (
    <div className="min-h-screen bg-gray-100 text-gray-900">
      {/* Horn√≠ li≈°ta */}
      <div className="bg-mint-500 text-white text-center py-4 text-2xl font-bold">
        Hello, Cycling World! üö¥‚Äç‚ôÇÔ∏è
      </div>

      {/* Li≈°ta s roky */}
      <div className="bg-gray-700 text-white py-2 flex justify-center gap-4">
        {Object.keys(eventsByYear)
          .sort((a, b) => b - a) // ≈òad√≠me od nejnovƒõj≈°√≠ho
          .map((year) => (
            <button
              key={year}
              onClick={() => setSelectedYear(Number(year))}
              className={`px-4 py-1 font-semibold ${
                selectedYear === Number(year) ? "underline" : ""
              }`}
            >
              {year}
            </button>
          ))}
      </div>

      {/* Seznam event≈Ø */}
      <div className="p-4">
        {eventsByYear[selectedYear]
          .filter((event) => !event.hidden) // Skryjeme eventy s hidden: true
          .map((event, index) => (
            <div
              key={index}
              className="bg-white shadow-md p-4 my-2 cursor-pointer hover:bg-gray-200"
              onClick={() => navigate(`/event/${event.name.replace(/\s+/g, "-").toLowerCase()}`)}
            >
              <span className="font-bold">{event.name}</span> - {event.date}
            </div>
          ))}
      </div>
    </div>
  );
};

export default Home;
