import React from "react";
import { useParams, useNavigate } from "react-router-dom";

// 🔥 Dynamický import JSON souboru
const loadEventData = (eventName) => {
  try {
    return require(`./events/${eventName.replace(/\s+/g, "-").toLowerCase()}.json`);
  } catch (error) {
    console.error("❌ Event data not found:", eventName);
    return null;
  }
};

const EventDetail = () => {
  const { eventName } = useParams();
  const navigate = useNavigate();
  const eventData = loadEventData(eventName);

  if (!eventData) {
    return (
      <div className="p-4 text-center">
        <h1 className="text-2xl font-bold text-red-600">❌ Event data not found</h1>
        <button onClick={() => navigate("/")} className="mt-4 bg-gray-300 p-2 rounded">
          ⬅️ Back to Events
        </button>
      </div>
    );
  }

  return (
    <div className="p-4">
      {/* Tlačítko zpět */}
      <button onClick={() => navigate("/")} className="bg-gray-300 p-2 rounded">
        ⬅️ Back to Events
      </button>

      {/* Název eventu */}
      <h1 className="text-3xl font-bold">{eventData.name}</h1>
      <p className="text-gray-600">{eventData.date}</p>

      {/* Odkaz na program */}
      {eventData.schedule && (
        <a href={eventData.schedule} target="_blank" rel="noopener noreferrer" className="text-blue-500 underline block mt-2">
          📄 Event Schedule
        </a>
      )}

      {/* Seznam dnů */}
      {eventData.disciplines.map((day, dayIndex) => (
        <div key={dayIndex} className="mt-4">
          {/* Klikací nadpis dne */}
          <h3
            className="text-xl font-semibold cursor-pointer bg-gray-200 p-2"
            onClick={() => document.getElementById(`day-${dayIndex}`).classList.toggle("hidden")}
          >
            Day {day.day} - {day.date} 
          </h3>

         {/* Seznam disciplín */}
<div id={`day-${dayIndex}`} className="hidden p-2">
  {day.events
            .filter(event => !event.hidden) //Skryjeme disciplíny, které mají hidden:true
            .map((event, eventIndex) => (
              <div key={eventIndex} className="flex justify-between bg-white p-2 my-1 shadow">



      {/* 🏁 Klikací název disciplíny → vede na Live Results */}
      <span
        className="text-blue-500 cursor-pointer underline"
        onClick={() => navigate(`/event/${eventName}/discipline/${event.name.replace(/\s+/g, "-").toLowerCase()}`)}
      >
        {event.listDisplayName || event.name} {/* 🆕 Zobrazíme nový název */}
      </span>

      {/* 📄 PDF tlačítka pro StartList & Results */}
      <div>
        {event.startList && (
          <a href={event.startList} target="_blank" rel="noopener noreferrer" className="ml-2 bg-gray-300 p-1 rounded">
            📄 StartList
          </a>
        )}
        {event.results && (
          <a href={event.results} target="_blank" rel="noopener noreferrer" className="ml-2 bg-gray-300 p-1 rounded">
            🏆 Results
          </a>
        )}
      </div>
    </div>
  ))}
</div>

        </div>
      ))}
    </div>
  );
};

export default EventDetail;
